mapper.select:
select 

from YMMG_BPM_BIZ_LINK
where ID = #{id,jdbcType=VARCHAR}
  and BIZ_CODE = #{bizCode,jdbcType=VARCHAR}

mapper.delete:
delete from YMMG_BPM_BIZ_LINK
where ID = #{id,jdbcType=VARCHAR}
  and BIZ_CODE = #{bizCode,jdbcType=VARCHAR}

mapper.update:
update YMMG_BPM_BIZ_LINK


    DEF_ID = #{defId,jdbcType=VARCHAR},


    INST_ID = #{instId,jdbcType=VARCHAR},


    BIZ_ID = #{bizId,jdbcType=VARCHAR},


where ID = #{id,jdbcType=VARCHAR}
  and BIZ_CODE = #{bizCode,jdbcType=VARCHAR}

update YMMG_BPM_BIZ_LINK
set DEF_ID = #{defId,jdbcType=VARCHAR},
  INST_ID = #{instId,jdbcType=VARCHAR},
  BIZ_ID = #{bizId,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}
  and BIZ_CODE = #{bizCode,jdbcType=VARCHAR}
